import i18next from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';

// Fichiers de traduction
const resources = {
  en: {
    translation: {
      // Textes en anglais
      appName: 'CryptoGrind',
      home: 'Home',
      profile: 'Profile',
      coffee: 'Coffee',
      missions: 'Missions',
      notifications: 'Notifications',
      notificationWen: 'WEN Notification',
      connectWallet: 'Connect Wallet',
      disconnectWallet: 'Disconnect Wallet',
      connectX: 'Connect X',
      disconnectX: 'Disconnect X',
      resetLocalStorage: 'Reset LocalStorage',
      // En-têtes du tableau dans Home.js
      table_name: 'Name',
      table_type: 'Type',
      table_mint_date: 'Mint Date',
      table_mint_time: 'Mint Time',
      table_provisional_date: 'Provisional Date',
      table_note: 'Note',
      table_price: 'Price',
      table_image: 'Image',
      table_links: 'Links',
      table_actions: 'Actions',
    },
  },
  fr: {
    translation: {
      // Textes en français
      appName: 'CryptoGrind',
      home: 'Accueil',
      profile: 'Profil',
      coffee: 'Coffee',
      missions: 'Missions',
      notifications: 'Notifications',
      notificationWen: 'Notification WEN',
      connectWallet: 'Connexion Wallet',
      disconnectWallet: 'Déconnexion Wallet',
      connectX: 'Connexion X',
      disconnectX: 'Déconnexion X',
      resetLocalStorage: 'Réinitialiser localStorage',
      // En-têtes du tableau dans Home.js
      table_name: 'Nom',
      table_type: 'Type',
      table_mint_date: 'Date de mint',
      table_mint_time: 'Heure de mint',
      table_provisional_date: 'Date provisoire',
      table_note: 'Note',
      table_price: 'Prix',
      table_image: 'Image',
      table_links: 'Liens',
      table_actions: 'Actions',
    },
  },
};

i18next
  .use(LanguageDetector) // Détecte la langue du navigateur
  .use(initReactI18next) // Intègre avec React
  .init({
    resources,
    fallbackLng: 'en', // Langue par défaut : anglais
    interpolation: {
      escapeValue: false, // React échappe déjà les valeurs
    },
  });

export default i18next;
