import React, { useState, useEffect, useContext } from 'react';
import { useTranslation } from 'react-i18next';
import { GrindContext } from '../App';

function NotificationWen() {
  const { t } = useTranslation();
  const { account } = useContext(GrindContext);
  const [notifications, setNotifications] = useState([]);
  const [projects, setProjects] = useState([]);
  const [editingNotifId, setEditingNotifId] = useState(null);
  const [editForm, setEditForm] = useState(null);

  useEffect(() => {
    if (account) {
      const savedNotifications = localStorage.getItem(`wenNotifications_${account}`);
      const savedProjects = localStorage.getItem(`projects_${account}`);
      setNotifications(savedNotifications ? JSON.parse(savedNotifications) : []);
      setProjects(savedProjects ? JSON.parse(savedProjects) : []);
    } else {
      setNotifications([]);
      setProjects([]);
    }
  }, [account]);

  useEffect(() => {
    if (!account || notifications.length > 0) return;

    const savedProjects = localStorage.getItem(`projects_${account}`);
    if (!savedProjects) return;

    try {
      const projects = JSON.parse(savedProjects);
      const projectsWithoutMintDate = projects.filter((project) => !project.mintDate);

      if (projectsWithoutMintDate.length > 0) {
        const newWenNotifications = projectsWithoutMintDate.map((project) => ({
          id: `wen_${project.id}_${Date.now()}`,
          project,
          date: new Date().toISOString(),
        }));

        localStorage.setItem(`wenNotifications_${account}`, JSON.stringify(newWenNotifications));
        setNotifications(newWenNotifications);
      }
    } catch (error) {}
  }, [account, notifications.length]);

  useEffect(() => {
    if (account) {
      const savedHistoricalNotifications = localStorage.getItem(`notifications_${account}`);
      const historicalNotifications = savedHistoricalNotifications
        ? JSON.parse(savedHistoricalNotifications)
        : [];
      const today = new Date();
      const tomorrow = new Date(today);
      tomorrow.setDate(today.getDate() + 1);

      const isToday = (date) => {
        if (!date) return false;
        const projectDate = new Date(date);
        if (isNaN(projectDate.getTime())) return false;
        const todayStr = today.toISOString().split('T')[0];
        const projectDateStr = projectDate.toISOString().split('T')[0];
        return projectDateStr === todayStr;
      };

      const isTomorrow = (date) => {
        if (!date) return false;
        const projectDate = new Date(date);
        if (isNaN(projectDate.getTime())) return false;
        const tomorrowStr = tomorrow.toISOString().split('T')[0];
        const projectDateStr = projectDate.toISOString().split('T')[0];
        return projectDateStr === tomorrowStr;
      };

      const todayTomorrowCount = projects.filter(
        (project) => isToday(project.mintDate) || isTomorrow(project.mintDate)
      ).length;
      const totalCount = notifications.length + historicalNotifications.length + todayTomorrowCount;
      localStorage.setItem(`notificationCount_${account}`, totalCount.toString());
      localStorage.setItem(`wenNotificationCount_${account}`, notifications.length.toString());
    }
  }, [notifications, projects, account]);

  const handleEdit = (notif) => {
    setEditingNotifId(notif.id);
    setEditForm({
      name: notif.project.name,
      type: notif.project.type,
      customType: notif.project.customType || '',
      mintDate: notif.project.mintDate || '',
      mintTime: notif.project.mintTime || '',
      provisionalDate: notif.project.provisionalDate || '',
      notificationFrequency: notif.project.notificationFrequency || '',
      chain: notif.project.chain || 'Abstract',
      customChain: notif.project.chain === 'Custom' ? notif.project.chain : '',
      note: notif.project.note || 1,
      isFree: notif.project.isFree || false,
      price: notif.project.price || '',
      image: notif.project.image || null,
      imageUrl: notif.project.image || '',
      telegramLink: notif.project.telegramLink || '',
      xLink: notif.project.xLink || '',
      discordLink: notif.project.discordLink || '',
      websiteLink: notif.project.websiteLink || '',
      platformLink: notif.project.platformLink || '',
      organizerLink: notif.project.organizerLink || '',
    });
  };

  const handleSave = (notifId) => {
    const updatedNotifications = notifications.map((notif) => {
      if (notif.id === notifId) {
        const updatedProject = {
          ...notif.project,
          name: editForm.name,
          type: editForm.type,
          customType: editForm.type === 'Custom' ? editForm.customType : '',
          mintDate: editForm.mintDate || null,
          mintTime: editForm.mintTime || null,
          provisionalDate: editForm.provisionalDate || null,
          notificationFrequency: editForm.provisionalDate ? editForm.notificationFrequency || null : null,
          chain: editForm.chain === 'Custom' ? editForm.customChain : editForm.chain,
          note: parseInt(editForm.note),
          isFree: editForm.isFree,
          price: editForm.isFree ? '' : editForm.price,
          image: editForm.imageUrl || editForm.image,
          telegramLink: editForm.telegramLink || null,
          xLink: editForm.xLink || null,
          discordLink: editForm.discordLink || null,
          websiteLink: editForm.websiteLink || null,
          platformLink: editForm.platformLink || null,
          organizerLink: editForm.organizerLink || null,
        };

        if (editForm.mintDate) {
          const savedNotifications = localStorage.getItem(`notifications_${account}`);
          const normalNotifications = savedNotifications ? JSON.parse(savedNotifications) : [];
          normalNotifications.push({
            id: `normal_${notif.project.id}_${Date.now()}`,
            project: updatedProject,
            date: new Date().toISOString(),
          });
          localStorage.setItem(`notifications_${account}`, JSON.stringify(normalNotifications));

          const updatedProjects = projects.map((proj) =>
            proj.id === notif.project.id ? updatedProject : proj
          );
          localStorage.setItem(`projects_${account}`, JSON.stringify(updatedProjects));
          setProjects(updatedProjects);

          return null;
        }

        return { ...notif, project: updatedProject };
      }
      return notif;
    }).filter((notif) => notif !== null);

    localStorage.setItem(`wenNotifications_${account}`, JSON.stringify(updatedNotifications));
    setNotifications(updatedNotifications);

    const updatedProjects = projects.map((proj) =>
      proj.id === updatedNotifications.find((n) => n.id === notifId)?.project.id
        ? updatedNotifications.find((n) => n.id === notifId).project
        : proj
    );
    localStorage.setItem(`projects_${account}`, JSON.stringify(updatedProjects));
    setProjects(updatedProjects);

    setEditingNotifId(null);
    setEditForm(null);
  };

  const handleCancel = () => {
    setEditingNotifId(null);
    setEditForm(null);
  };

  const handleDelete = (notifId) => {
    const updatedNotifications = notifications.filter((notif) => notif.id !== notifId);
    localStorage.setItem(`wenNotifications_${account}`, JSON.stringify(updatedNotifications));
    setNotifications(updatedNotifications);
  };

  return (
    <div className="min-h-screen p-6" style={{ backgroundColor: '#00DDAF' }}>
      <h1 className="text-4xl font-bold text-gray-800 mb-6">{t('notificationWen')}</h1>
      <div className="mb-6">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">{t('notificationWen')}</h2>
        {notifications.length === 0 ? (
          <p className="text-gray-700">{t('no_wen_notifications')}</p>
        ) : (
          <div className="space-y-4">
            {notifications
              .sort((a, b) => new Date(b.date) - new Date(a.date))
              .map((notif) => (
                <div
                  key={notif.id}
                  className="p-4 bg-white shadow-md rounded-lg transition-all hover:shadow-lg"
                >
                  {editingNotifId === notif.id ? (
                    <div className="space-y-4">
                      <div>
                        <label className="block text-gray-700 font-semibold">{t('project_name')}</label>
                        <input
                          type="text"
                          value={editForm.name}
                          onChange={(e) => setEditForm({ ...editForm, name: e.target.value })}
                          className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                          required
                        />
                      </div>
                      <div>
                        <label className="block text-gray-700 font-semibold">{t('type')}</label>
                        <select
                          value={editForm.type}
                          onChange={(e) => setEditForm({ ...editForm, type: e.target.value })}
                          className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                        >
                          <option value="TGE">{t('project_type_tge')}</option>
                          <option value="WL">{t('project_type_wl')}</option>
                          <option value="OG">{t('project_type_og')}</option>
                          <option value="Waitlist">{t('project_type_waitlist')}</option>
                          <option value="Interested">{t('project_type_interested')}</option>
                          <option value="Airdrop">{t('project_type_airdrop')}</option>
                          <option value="Launch">{t('project_type_launch')}</option>
                          <option value="Custom">{t('project_type_custom')}</option>
                        </select>
                        {editForm.type === 'Custom' && (
                          <input
                            type="text"
                            value={editForm.customType}
                            onChange={(e) => setEditForm({ ...editForm, customType: e.target.value })}
                            className="w-full p-2 border rounded-lg mt-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                            placeholder={t('custom_type_placeholder')}
                          />
                        )}
                      </div>
                      <div>
                        <label className="block text-gray-700 font-semibold">{t('mint_date')}</label>
                        <input
                          type="date"
                          value={editForm.mintDate}
                          onChange={(e) => setEditForm({ ...editForm, mintDate: e.target.value })}
                          className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                          placeholder={t('date_format_placeholder')}
                        />
                      </div>
                      <div>
                        <label className="block text-gray-700 font-semibold">{t('mint_time')}</label>
                        <input
                          type="time"
                          value={editForm.mintTime}
                          onChange={(e) => setEditForm({ ...editForm, mintTime: e.target.value })}
                          className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                        />
                      </div>
                      <div>
                        <label className="block text-gray-700 font-semibold">{t('provisional_date')}</label>
                        <input
                          type="text"
                          value={editForm.provisionalDate}
                          onChange={(e) => setEditForm({ ...editForm, provisionalDate: e.target.value })}
                          className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                          placeholder={t('provisional_date_placeholder')}
                        />
                      </div>
                      {editForm.provisionalDate && (
                        <div>
                          <label className="block text-gray-700 font-semibold">{t('notification_frequency')}</label>
                          <select
                            value={editForm.notificationFrequency}
                            onChange={(e) => setEditForm({ ...editForm, notificationFrequency: e.target.value })}
                            className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                          >
                            <option value="">{t('no_notification')}</option>
                            <option value="1">{t('1_day')}</option>
                            <option value="3">{t('3_days')}</option>
                            <option value="7">{t('7_days')}</option>
                            <option value="15">{t('15_days')}</option>
                            <option value="30">{t('30_days')}</option>
                          </select>
                        </div>
                      )}
                      <div>
                        <label className="block text-gray-700 font-semibold">{t('chain')}</label>
                        <select
                          value={editForm.chain}
                          onChange={(e) => setEditForm({ ...editForm, chain: e.target.value })}
                          className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                        >
                          <option value="Abstract">{t('chain_abstract')}</option>
                          <option value="ETH">{t('chain_eth')}</option>
                          <option value="BTC">{t('chain_btc')}</option>
                          <option value="SOL">{t('chain_sol')}</option>
                          <option value="BNB">{t('chain_bnb')}</option>
                          <option value="Custom">{t('chain_custom')}</option>
                        </select>
                        {editForm.chain === 'Custom' && (
                          <input
                            type="text"
                            value={editForm.customChain}
                            onChange={(e) => setEditForm({ ...editForm, customChain: e.target.value })}
                            className="w-full p-2 border rounded-lg mt-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                            placeholder={t('custom_chain_placeholder')}
                          />
                        )}
                      </div>
                      <div>
                        <label className="block text-gray-700 font-semibold">{t('note')}</label>
                        <input
                          type="number"
                          min="1"
                          max="10"
                          value={editForm.note}
                          onChange={(e) => setEditForm({ ...editForm, note: e.target.value })}
                          className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                          required
                        />
                      </div>
                      <div>
                        <label className="flex items-center text-gray-700 font-semibold">
                          <input
                            type="checkbox"
                            checked={editForm.isFree}
                            onChange={(e) => setEditForm({ ...editForm, isFree: e.target.checked })}
                            className="mr-2"
                          />
                          {t('free_mint')}
                        </label>
                        {!editForm.isFree && (
                          <div className="mt-2">
                            <label className="block text-gray-700 font-semibold">{t('price')}</label>
                            <input
                              type="text"
                              value={editForm.price}
                              onChange={(e) => setEditForm({ ...editForm, price: e.target.value })}
                              className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                              placeholder={t('price_placeholder')}
                            />
                          </div>
                        )}
                      </div>
                      <div>
                        <label className="block text-gray-700 font-semibold">{t('image')}</label>
                        <div className="mt-1">
                          <label className="block text-gray-700 font-semibold">{t('upload_image')}</label>
                          <input
                            type="file"
                            accept="image/*"
                            onChange={(e) => setEditForm({ ...editForm, image: e.target.files[0], imageUrl: '' })}
                            className="w-full p-2"
                          />
                        </div>
                        <div className="mt-2">
                          <label className="block text-gray-700 font-semibold">{t('image_url')}</label>
                          <input
                            type="text"
                            value={editForm.imageUrl}
                            onChange={(e) => setEditForm({ ...editForm, imageUrl: e.target.value, image: null })}
                            className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            placeholder={t('image_url_placeholder')}
                          />
                        </div>
                      </div>
                      <div>
                        <label className="block text-gray-700 font-semibold">{t('links_optional')}</label>
                        <div className="space-y-2 mt-1">
                          <div>
                            <label className="block text-gray-700 font-semibold">{t('telegram')}</label>
                            <input
                              type="text"
                              value={editForm.telegramLink}
                              onChange={(e) => setEditForm({ ...editForm, telegramLink: e.target.value })}
                              className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                              placeholder={t('telegram_link_placeholder')}
                            />
                          </div>
                          <div>
                            <label className="block text-gray-700 font-semibold">{t('x')}</label>
                            <input
                              type="text"
                              value={editForm.xLink}
                              onChange={(e) => setEditForm({ ...editForm, xLink: e.target.value })}
                              className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                              placeholder={t('x_link_placeholder')}
                            />
                          </div>
                          <div>
                            <label className="block text-gray-700 font-semibold">{t('discord')}</label>
                            <input
                              type="text"
                              value={editForm.discordLink}
                              onChange={(e) => setEditForm({ ...editForm, discordLink: e.target.value })}
                              className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                              placeholder={t('discord_link_placeholder')}
                            />
                          </div>
                          <div>
                            <label className="block text-gray-700 font-semibold">{t('website')}</label>
                            <input
                              type="text"
                              value={editForm.websiteLink}
                              onChange={(e) => setEditForm({ ...editForm, websiteLink: e.target.value })}
                              className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                              placeholder={t('website_link_placeholder')}
                            />
                          </div>
                          <div>
                            <label className="block text-gray-700 font-semibold">{t('platform')}</label>
                            <input
                              type="text"
                              value={editForm.platformLink}
                              onChange={(e) => setEditForm({ ...editForm, platformLink: e.target.value })}
                              className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                              placeholder={t('platform_link_placeholder')}
                            />
                          </div>
                          <div>
                            <label className="block text-gray-700 font-semibold">{t('organizer')}</label>
                            <input
                              type="text"
                              value={editForm.organizerLink}
                              onChange={(e) => setEditForm({ ...editForm, organizerLink: e.target.value })}
                              className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                              placeholder={t('organizer_link_placeholder')}
                            />
                          </div>
                        </div>
                      </div>
                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleSave(notif.id)}
                          className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600"
                        >
                          {t('save')}
                        </button>
                        <button
                          onClick={handleCancel}
                          className="bg-gray-500 text-white px-4 py-2 rounded-lg hover:bg-gray-600"
                        >
                          {t('cancel')}
                        </button>
                        <button
                          onClick={() => handleDelete(notif.id)}
                          className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600"
                        >
                          {t('delete')}
                        </button>
                      </div>
                    </div>
                  ) : (
                    <div>
                      <p className="text-gray-800">
                        {t('reminder_for_project')}{' '}
                        <span className="font-semibold">{notif.project.name}</span> (
                        {new Date(notif.date).toLocaleString()}).
                      </p>
                      <p className="text-gray-700">
                        <strong>{t('type')} :</strong> {notif.project.type}
                        {notif.project.customType && ` (${notif.project.customType})`}
                      </p>
                      <p className="text-gray-700">
                        <strong>{t('mint_date')} :</strong> {notif.project.mintDate || 'N/A'}{' '}
                        {notif.project.mintTime || ''}
                      </p>
                      <p className="text-gray-700">
                        <strong>{t('note')} :</strong> {notif.project.note || 'N/A'}/10
                      </p>
                      <p className="text-gray-700">
                        <strong>{t('price')} :</strong>{' '}
                        {notif.project.isFree ? t('free_mint') : notif.project.price || 'N/A'}
                      </p>
                      {notif.project.image && (
                        <div className="mt-2">
                          <img
                            src={notif.project.image}
                            alt={notif.project.name}
                            className="w-32 h-32 object-cover rounded-lg"
                          />
                        </div>
                      )}
                      <div className="mt-2">
                        <p className="text-gray-700 font-semibold">{t('links_optional')} :</p>
                        <div className="flex flex-col space-y-1">
                          {notif.project.telegramLink && (
                            <a
                              href={notif.project.telegramLink}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-blue-500 hover:underline"
                            >
                              Telegram
                            </a>
                          )}
                          {notif.project.xLink && (
                            <a
                              href={notif.project.xLink}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-blue-500 hover:underline"
                            >
                              X
                            </a>
                          )}
                          {notif.project.discordLink && (
                            <a
                              href={notif.project.discordLink}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-blue-500 hover:underline"
                            >
                              Discord
                            </a>
                          )}
                          {notif.project.websiteLink && (
                            <a
                              href={notif.project.websiteLink}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-blue-500 hover:underline"
                            >
                              {t('website')}
                            </a>
                          )}
                          {notif.project.platformLink && (
                            <a
                              href={notif.project.platformLink}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-blue-500 hover:underline"
                            >
                              {t('platform')}
                            </a>
                          )}
                          {notif.project.organizerLink && (
                            <a
                              href={notif.project.organizerLink}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-blue-500 hover:underline"
                            >
                              {t('organizer')}
                            </a>
                          )}
                        </div>
                      </div>
                      <button
                        onClick={() => handleEdit(notif)}
                        className="mt-2 bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
                      >
                        {t('edit')}
                      </button>
                    </div>
                  )}
                </div>
              ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default NotificationWen;
