import React, { useState, useEffect } from 'react';
import { WagmiConfig, createConfig, useAccount, useConnect, useDisconnect } from 'wagmi';
import { createPublicClient, http } from 'viem';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { injected, walletConnect } from '@wagmi/connectors';
import Header from './components/Header';
import Home from './pages/Home';
import Notifications from './pages/Notifications';
import Profile from './pages/Profile';
import Missions from './pages/Missions';
import NotificationWen from './components/NotificationWen';
import AddProject from './components/AddProject';

const abstractTestnet = {
  id: 11124,
  name: 'Abstract Testnet',
  network: 'abstract-testnet',
  nativeCurrency: {
    name: 'Ether',
    symbol: 'ETH',
    decimals: 18,
  },
  rpcUrls: {
    default: {
      http: ['https://api.testnet.abs.xyz'],
    },
    public: {
      http: ['https://api.testnet.abs.xyz'],
    },
  },
  blockExplorers: {
    default: { name: 'AbstractScan', url: 'https://sepolia.abscan.org' },
  },
  testnet: true,
};

const queryClient = new QueryClient();

const config = createConfig({
  chains: [abstractTestnet],
  connectors: [
    injected({ target: 'metaMask' }), // MetaMask
    walletConnect({ projectId: '2471fc52b74931df68f43e44f96078b0' }), // Ton projectId
  ],
  client: ({ chain }) =>
    createPublicClient({
      chain,
      transport: http(),
    }),
});

export const GrindContext = React.createContext();

// Composant enfant pour gérer les connexions
function WalletConnector({ setAccount }) {
  const { address, isConnected } = useAccount();
  const { connect, connectors, error: connectError, isLoading } = useConnect();
  const { disconnect } = useDisconnect();

  // Loguer les connecteurs disponibles
  useEffect(() => {
    console.log('Connecteurs disponibles:', connectors);
    connectors.forEach((connector) => {
      console.log(`Connecteur ${connector.name}:`, {
        id: connector.id,
        ready: connector.ready,
      });
    });
  }, [connectors]);

  // Synchroniser l'adresse wagmi avec l'état local
  useEffect(() => {
    if (isConnected && address) {
      const normalizedAccount = address.toLowerCase();
      console.log('Compte connecté via wagmi:', normalizedAccount);
      setAccount(normalizedAccount);
    } else {
      console.log('Aucun compte connecté');
      setAccount(null);
    }
  }, [address, isConnected, setAccount]);

  return (
    <div className="p-4">
      {isConnected ? (
        <div>
          <p>Connecté : {address}</p>
          <button
            onClick={() => disconnect()}
            className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700"
          >
            Déconnexion
          </button>
        </div>
      ) : (
        <div>
          {connectors.length === 0 && <p>Aucun connecteur disponible</p>}
          {connectors.map((connector) => (
            <button
              key={connector.id}
              onClick={() => connect({ connector })}
              disabled={!connector.ready || isLoading}
              className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 mr-2"
            >
              {isLoading ? 'Connexion...' : `Connecter via ${connector.name}`}
            </button>
          ))}
          {connectError && <p className="text-red-600">{connectError.message}</p>}
        </div>
      )}
    </div>
  );
}

function App() {
  const [grindBalance, setGrindBalance] = useState(0);
  const [account, setAccount] = useState(null);
  const [projects, setProjects] = useState([]);

  // Récupérer le solde ETH manuellement
  useEffect(() => {
    if (account) {
      const checkBalanceManually = async () => {
        try {
          const client = createPublicClient({
            chain: abstractTestnet,
            transport: http(),
          });
          const balance = await client.getBalance({ address: account });
          console.log('Solde manuel ETH:', balance.toString(), 'wei');
          console.log('Solde manuel ETH (formatté):', Number(balance) / 1e18, 'ETH');
          setGrindBalance(balance.toString()); // Solde en wei
        } catch (error) {
          console.error('Erreur requête manuelle solde:', error);
        }
      };
      checkBalanceManually();
    }
  }, [account]);

  // Charger les projets depuis localStorage
  useEffect(() => {
    if (account) {
      const savedProjects = localStorage.getItem(`projects_${account}`);
      console.log('Chargement projets depuis localStorage:', savedProjects);
      setProjects(savedProjects ? JSON.parse(savedProjects) : []);
    } else {
      setProjects([]);
      console.log('Aucun account, réinitialisation projets');
    }
  }, [account]);

  // Synchroniser localStorage avec l'état projects
  useEffect(() => {
    if (account) {
      console.log('Sauvegarde projets dans localStorage:', projects);
      localStorage.setItem(`projects_${account}`, JSON.stringify(projects));
    }
  }, [projects, account]);

  const handleAddProject = (project) => {
    setProjects((prevProjects) => {
      const updatedProjects = [...prevProjects, project];
      console.log('Nouveau projet ajouté:', project);
      return updatedProjects;
    });
  };

  return (
    <WagmiConfig config={config}>
      <QueryClientProvider client={queryClient}>
        <GrindContext.Provider value={{ grindBalance, account }}>
          <Router>
            <WalletConnector setAccount={setAccount} />
            <Header />
            <Routes>
              <Route
                path="/"
                element={
                  <Home
                    projects={projects}
                    setProjects={setProjects}
                    handleAddProject={handleAddProject}
                  />
                }
              />
              <Route path="/notifications" element={<Notifications />} />
              <Route path="/profile" element={<Profile />} />
              <Route path="/missions" element={<Missions />} />
              <Route path="/lottery" element={<NotificationWen projects={projects} />} />
              <Route path="/add-project" element={<AddProject onAddProject={handleAddProject} />} />
            </Routes>
          </Router>
        </GrindContext.Provider>
      </QueryClientProvider>
    </WagmiConfig>
  );
}

export default App;
